	}
	if err := ipv4Conn.SetTOS(0x10); err != nil {
		fmt.Println("Error setting TOS:", err)
	}
	if err := ipv4Conn.SetTTL(64); err != nil {
		fmt.Println("Error setting TTL:", err)
	}

	_, err = conn.Write([]byte("HELLO-R-U-THERE-ACK"))
	if err != nil {
		fmt.Println("Error writing:", err)
	}

	conn.Close()
}

