package main

import (
	"net"
	"syscall"
)

func main() {
	l, err := net.Listen("tcp6", ":1024")
	if err != nil {
		panic(err)
	}
	defer l.Close()

	for {
		conn, err := l.Accept()
		if err != nil {
			panic(err)
		}
		go handleConnection(conn)
	}
}

func handleConnection(conn net.Conn) {
	addr := conn.RemoteAddr().String()
	if !isIPv6(addr) {
		return
	}

	setIPv6Options(conn, 0x20, 5)

	_, err := conn.Write([]byte("HELLO-R-U-THERE-ACK"))
	if err != nil {
		panic(err)
	}

	conn.Close()
}

func isIPv6(s string) bool {
	_, err := net.ResolveTCPAddr("ipv6", s)
	return err == nil
}

func setIPv6Options(conn net.Conn, trafficClass, hopLimit uint8) {
	fd := conn.Fd()
	if fd < 0 {
		return
	}

	if err := syscall.Setsockopt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_TCLASS, &trafficClass); err != nil {
		panic(err)
	}

	if err := syscall.Setsockopt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_HOPLIMIT, &hopLimit); err != nil {
		panic(err)
	}
}

