func remoteCommand(ctx context.Context) error {
	flagAddress := "remote.server:50051"
	flagTimeout := 10
	flagEnableProfiling := true
	flagDisableProfiling := false
	flagRetrieveSnapshot := false
	flagSnapshot := "snapshot123"

	dialOptions := []grpc.DialOption{
		grpc.WithInsecure(),
		grpc.WithBlock(),
	}
	if flagTimeout > 0 {
		dialOptions = append(dialOptions, grpc.WithTimeout(time.Duration(flagTimeout)*time.Second))
	}

	conn, err := grpc.Dial(flagAddress, dialOptions...)
	if err != nil {
		return fmt.Errorf("failed to connect: %v", err)
	}
	defer conn.Close()

	client := profiling.NewProfilingClient(conn)

	var err error
	if flagEnableProfiling {
		_, err = client.Enable(ctx, &profiling.Empty{})
		if err != nil {
			return fmt.Errorf("failed to enable profiling: %v", err)
		}
	} else if flagDisableProfiling {
		_, err = client.Disable(ctx, &profiling.Empty{})
		if err != nil {
			return fmt.Errorf("failed to disable profiling: %v", err)
		}
	} else if flagRetrieveSnapshot {
		req := &profiling.RetrieveSnapshotRequest{
			SnapshotId: flagSnapshot,
		}
		_, err = client.RetrieveSnapshot(ctx, req)
		if err != nil {
			return fmt.Errorf("failed to retrieve snapshot: %v", err)
		}
	} else {
		return errors.New("no operation specified")
	}

	return nil
}

