func doClientWork() {
	conn, err := grpc.Dial("localhost:1234", grpc.WithInsecure())
	if err != nil {
		log.Fatalf("Failed to connect: %v", err)
	}
	defer conn.Close()

	client := examplepb.NewClient(conn)
	ctx := context.Background()

	// Unary RPC
	resp, err := client.Hello(ctx, &examplepb.HelloRequest{})
	if err != nil {
		log.Fatalf("Error in unary call: %v", err)
	}
	fmt.Println("Unary response:", resp.Message)

	// Bidirectional streaming
	stream, err := client.Channel(ctx)
	if err != nil {
		log.Fatalf("Error creating stream: %v", err)
	}

	// Goroutine to send messages
	go func() {
		for {
			time.Sleep(time.Second)
			if err := stream.Send(&examplepb.Message{Content: "Streaming message"}); err != nil {
				log.Printf("Error sending: %v", err)
				return
			}
		}
	}()

	// Main goroutine to receive messages
	for {
		msg, err := stream.Recv()
		if err != nil {
			log.Printf("Error receiving: %v", err)
			break
		}
		fmt.Println("Received:", msg.Content)
	}
}

