	servers  *CSIDriverServers
	wg       sync.WaitGroup
	running  bool
	lock     sync.Mutex
	creds    *CSICreds
	logGRPC  LogGRPC
}

func NewCSIDriver(servers *CSIDriverServers) *CSIDriver {
	return &CSIDriver{
		servers: servers,
		wg:      sync.WaitGroup{},
		running: false,
		lock:    sync.Mutex{},
		creds:   &CSICreds{},
		logGRPC: LogGRPC{},
	}
}

func (c *CSIDriver) Start(listener net.Listener, interceptor grpc.UnaryServerInterceptor) error {
	c.lock.Lock()
	defer c.lock.Unlock()

	if c.running {
		return fmt.Errorf("server already running")
	}

	c.listener = &listener
	c.server = grpc.NewServer(
		grpc.UnaryInterceptor(interceptor),
	)

	c.servers.Register(c.server)

	c.wg.Add(1)
	go c.goServe(make(chan bool))

	c.running = true
	return nil
}

func (c *CSIDriver) goServe(stopChan chan<- bool) {
	c.lock.Lock()
	defer c.lock.Unlock()

	if !c.running {
		return
	}

	if err := c.server.Serve(c.listener); err != nil {
		return
	}

	stopChan <- true
}

func (c *CSIDriver) Stop() {
	c.lock.Lock()
	defer c.lock.Unlock()

	if !c.running {
		return
	}

	c.running = false
	c.server.Stop()
	c.wg.Wait()
}

func (c *CSIDriver) callInterceptor(ctx context.Context, req interface{}, serverInfo *grpc.UnaryServerInfo, handler grpc.UnaryHandler) (interface{}, error) {
	if !c.creds.Authenticate(ctx) {
		return nil, status.Error(status.PermissionDenied, "authentication failed")
	}

	c.logGRPC.Log(ctx, serverInfo, req)
	return handler(ctx, req)
}

