}

func (s *Server) Start() error {
	if s.Listener == nil || s.Server == nil {
		return errors.New("server not initialized")
	}
	return s.Server.Serve(s.Listener)
}

func (s *Server) Stop() {
	if s.Server != nil {
		s.Server.Stop()
	}
}

func NewServer(address string, tlsConf *tls.Config, srvKaOpts *keepalive.ServerParameters) (*Server, error) {
	listener, err := net.Listen("tcp", address)
	if err != nil {
		return nil, err
	}

	opts := []grpc.ServerOption{}

	// Handle TLS
	if tlsConf != nil {
		creds, err := grpc.NewServerCredentials(tlsConf)
		if err != nil {
			return nil, err
		}
		opts = append(opts, grpc.Creds(creds))
	}

	// Handle keepalive parameters
	if srvKaOpts != nil {
		opts = append(opts, grpc.KeepaliveParams(srvKaOpts))
	} else {
		// Set default keepalive parameters
		defaultKaOpts := &keepalive.ServerParameters{
			MaxConnectionIdle: 20 * time.Second,
			MaxConnectionAge:  20 * time.Second,
			EnforceParam:      true,
		}
		opts = append(opts, grpc.KeepaliveParams(defaultKaOpts))
	}

	// Set default message size
	opts = append(opts, grpc.MaxMessageSize(4<<20)) // 4MB

	// Create the server
	server := grpc.NewServer(opts...)

	return &Server{
		Listener: listener,
		Server:   server,
	}, nil
}

