		if err != nil {
			fmt.Printf("Failed to open channel: %v, retrying... (%d/%d)\n", err, i+1, maxRetries)
			time.Sleep(time.Second * time.Duration(i+1))
			continue
		}
		r.channel = ch
		args := map[string]interface{}{
			"x-message-ttl":   3600,
			"x-max-priority":  4,
		}
		if err := ch.QueueDeclare(
			r.queueName,
			false,
			false,
			false,
			false,
			args,
		); err != nil {
			fmt.Printf("Failed to declare queue: %v, retrying... (%d/%d)\n", err, i+1, maxRetries)
			time.Sleep(time.Second * time.Duration(i+1))
			continue
		}
		return
	}
	fmt.Println("Failed to connect to RabbitMQ after retries")
}

func (r *RabbitClient) PublishTgMessage(messageBag MessageBag) {
	body, err := json.Marshal(messageBag)
	if err != nil {
		fmt.Printf("Failed to marshal message: %v\n", err)
		return
	}
	for i := 0; i < maxRetries; i++ {
		if r.connection == nil || r.channel == nil {
			r.Connect()
		}
		if err := r.channel.Publish(
			"", // exchange
			r.queueName, // routing key
			false, // mandatory
			false, // immediate
			amqp.Publishing{
				Body: body,
			},
		); err != nil {
			fmt.Printf("Failed to publish message: %v, retrying... (%d/%d)\n", err, i+1, maxRetries)
			time.Sleep(time.Second * time.Duration(i+1))
		} else {
			return
		}
	}
	fmt.Println("Failed to publish message after retries")
}

func (r *RabbitClient) Close() {
	if r.connection != nil {
		r.connection.Close()
	}
	if r.channel != nil {
		r.channel.Close()
	}
}

func NewRabbitClient(url string, queueName string) *RabbitClient {
	client := &RabbitClient{
		url:       url,
		queueName: queueName,
	}
	client.Connect()
	return client
}

