import (
	"fmt"
	"os"
	"syscall"
	"unsafe"
)

// #cgo CFLAGS: -I/usr/include
// #cgo LDFLAGS: -luserns
import "C"

func spawnUserNamespaceCat(nsPath string, path string) ([]byte, error) {
	stdoutR, stdoutW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stdoutR.Close()
	defer stdoutW.Close()

	stderrR, stderrW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stderrR.Close()
	defer stderrW.Close()

	stdoutFD := stdoutW.Fd()
	stderrFD := stderrW.Fd()

	cErr := C.spawn_userns_cat(C.CString(nsPath), C.CString(path), C.int(stdoutFD), C.int(stderrFD))
	if cErr != 0 {
		return nil, fmt.Errorf("failed to spawn process: %v", cErr)
	}

	pid := int(cErr)

	proc, err := os.FindProcess(pid)
	if err != nil {
		return nil, err
	}

	if err = proc.Wait(); err != nil {
		return nil, err
	}

	var stdout []byte
	buf := make([]byte, 1024)
	for {
		n, err := stdoutR.Read(buf)
		if n > 0 {
			stdout = append(stdout, buf[:n]...)
		}
		if err == syscall.EAGAIN || err == syscall.EWOULDBLOCK {
			break
		}
		if err != nil {
			return nil, err
		}
	}

	return stdout, nil
}

