	writer *os.File
	output *fileCloner
}

func newlogRetriever(logFile string) (*logRetriever, error) {
	reader, writer, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	output := &fileCloner{writer: writer}
	if _, err := os.Stat(logFile); err == nil {
		file, err := os.Open(logFile)
		if err != nil {
			return nil, err
		}
