	set map[int64]struct{}
}

func (s *LinkedHashSetINT64) Get() []int64 {
	result := make([]int64, 0, len(s.set))
	for k := range s.set {
		result = append(result, k)
	}
	return result
}

type PaperWallet struct{}

type Chart struct {
	port int
	candles map[string][]Candle
	dataframe map[string]*Dataframe
	ordersIDsByPair map[string]*LinkedHashSetINT64
	orderByID map[int64]Order
	paperWallet *PaperWallet
	lastUpdate time.Time
	Mutex sync.Mutex
}

func (c *Chart) handleHealth(w http.ResponseWriter, r *http.Request) {
	c.Mutex.Lock()
	defer c.Mutex.Unlock()
	if c.lastUpdate.IsZero() {
		http.Error(w, "Not updated yet", http.StatusInternalServerError)
		return
	}
	w.WriteHeader(http.StatusOK)
	json.NewEncoder(w).Encode(map[string]interface{}{
		"status": "healthy",
		"last_update": c.lastUpdate.UTC().Format(time.RFC3339),
	})
}

func (c *Chart) handleData(w http.ResponseWriter, r *http.Request) {
	c.Mutex.Lock()
	defer c.Mutex.Unlock()
	pair := r.URL.Query().Get("pair")
	if pair == "" {
		http.Error(w, "Missing pair parameter", http.StatusBadRequest)
		return
	}
	candles, ok := c.candles[pair]
	df, ok2 := c.dataframe[pair]
	if !ok || !ok2 {
		http.Error(w, "No data for pair", http.StatusNotFound)
		return
	}
	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(map[string]interface{}{
		"candles": candles,
		"dataframe": df,
	})
}

func (c *Chart) handleTradingHistoryData(w http.ResponseWriter, r *http.Request) {
	c.Mutex.Lock()
	defer c.Mutex.Unlock()
	pair := r.URL.Query().Get("pair")
	if pair == "" {
		http.Error(w, "Missing pair parameter", http.StatusBadRequest)
		return
	}
	orderIDs := c.ordersIDsByPair[pair]
	if orderIDs == nil {
		http.Error(w, "No orders for pair", http.StatusNotFound)
		return
	}
	var orders []Order
	for _, id := range orderIDs.Get() {
		order, exists := c.orderByID[id]
		if exists {
			orders = append(orders, order)
		}
	}
	csvData := "id,side,quantity,price\n"
	for _, order := range orders {
		csvData += fmt.Sprintf("%d,%s,%d,%f\n", order.ID, order.Side, order.Quantity, order.Price)
	}
	w.Header().Set("Content-Type", "text/csv")
	w.Write([]byte(csvData))
}

func (c *Chart) Start() error {
	http.HandleFunc("/health", c.handleHealth)
	http.HandleFunc("/data", c.handleData)
	http.HandleFunc("/history", c.handleTradingHistoryData)
	err := http.ListenAndServe(fmt.Sprintf(":%d", c.port), nil)
	if err != nil {
		return err
	}
	return nil
}

