var responses = make(map[string]*http.Response)

func handle(w http.ResponseWriter, r *http.Request) {
	proxify := r.Header.Get("proxify")
	if proxify != "" {
		resp, ok := responses[proxify]
		if ok {
			w.WriteHeader(resp.StatusCode)
			w.Write(resp.Body)
			delete(responses, proxify)
		} else {
			http.Error(w, "No response found", http.StatusNotFound)
		}
	} else {
		http.Error(w, "Missing proxify header", http.StatusBadRequest)
	}
}

