	"time"
	"github.com/prometheus/client_golang/prometheus"
)

var (
	previousPacketCount int64
	startTime           time.Time
)

func ServeMetricsAt(addr string, c *collector.Collector) {
	uptimeGauge := prometheus.NewGauge(prometheus.GaugeOpts{
		Name: "uptime_seconds",
		Help: "Uptime in seconds",
	})
	packetCounter := prometheus.NewCounter(prometheus.CounterOpts{
		Name: "packet_count_total",
		Help: "Total number of packets",
	})
	prometheus.MustRegister(uptimeGauge, packetCounter)

	startTime = time.Now()

	http.HandleFunc("/metrics", func(w http.ResponseWriter, r *http.Request) {
		if c != nil {
			currentPacketCount := c.GetPacketCount()
			packetCounter.Add(float64(currentPacketCount - previousPacketCount))
			previousPacketCount = currentPacketCount
		}
		uptimeGauge.Set(time.Since(startTime).Seconds())
		prometheus.Handler().ServeHTTP(w, r)
	})

	go func() {
		err := http.ListenAndServe(addr, nil)
		if err != nil {
			panic(err)
		}
	}()
}

