package main

import (
	"flag"
	"fmt"
	"log"
	"net/http"
	"golang.org/x/net/webdav"
)

type CustomHandler struct {
	webdav.Handler
}

func (h *CustomHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	if r.Method == "PROPFIND" && r.URL.Path == "/litmus/propfind_invalid2" {
		log.Println("Handling Litmus test case: props: 3 (propfind_invalid2)")
		w.WriteHeader(400)
		w.Write([]byte("Special handling for Litmus test"))
		return
	}
	h.Handler.ServeHTTP(w, r)
}

func main() {
	port := flag.Int("port", 8080, "Port to listen on")
	flag.Parse()

	fs := webdav.MemFS{}
	ls := webdav.MemLS{}

	logger := func(r *http.Request, err error) {
		log.Printf("Request: %s %s, Error: %v", r.Method, r.URL.Path, err)
	}

	handler := webdav.Handler{
		FileSystem: fs,
		LockSystem: ls,
		Logger: logger,
	}

	customHandler := &CustomHandler{Handler: handler}

	http.Handle("/", customHandler)

	log.Println("Starting server on port", *port)
	if err := http.ListenAndServe(fmt.Sprintf(":%d", *port), nil); err != nil {
		log.Fatal("Server failed:", err)
	}
}

