import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import java.nio.charset.StandardCharsets;
import java.util.logging.Level;
import java.util.logging.Logger;

public class HttpDebugIdSender {

    private static final Logger LOGGER = Logger.getLogger(HttpDebugIdSender.class.getName());

    private final String distinctId;
    private final String infoId;
    private final String serverUrl;

    public HttpDebugIdSender(String serverUrl, String distinctId, String infoId) {
        this.serverUrl = serverUrl;
        this.distinctId = distinctId;
        this.infoId = infoId;
    }

    public void send() {
        try {
            sendHttpRequest(serverUrl, true);
        } catch (IOException e) {
            LOGGER.log(Level.SEVERE, "Error sending debug information", e);
        }
    }

    private void sendHttpRequest(String serverUrl, boolean isRedirects) throws IOException {
        HttpURLConnection connection = null;
        OutputStream out = null;
        BufferedOutputStream bout = null;
        ByteArrayOutputStream outContent = null;

        try {
            URL url = new URL(serverUrl);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setRequestProperty("Content-Type", "application/json; utf-8");
            connection.setRequestProperty("Accept", "application/json");
            connection.setDoOutput(true);

            String jsonInputString = "{\"distinctId\":\"" + distinctId + "\", \"infoId\":\"" + infoId + "\"}";

            outContent = new ByteArrayOutputStream();
            out = connection.getOutputStream();
            bout = new BufferedOutputStream(out);
            bout.write(jsonInputString.getBytes(StandardCharsets.UTF_8));
            bout.flush();

            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_MOVED_TEMP || responseCode == HttpURLConnection.HTTP_MOVED_PERM) {
                String redirectUrl = connection.getHeaderField("Location");
                if (isRedirects) {
                    sendHttpRequest(redirectUrl, false);
                } else {
                    LOGGER.log(Level.WARNING, "Redirect loop detected, stopping further redirects");
                }
            } else if (responseCode != HttpURLConnection.HTTP_OK) {
                LOGGER.log(Level.SEVERE, "Server returned non-OK response code: " + responseCode);
            }
        } finally {
            closeStream(outContent, out, bout, connection);
        }
    }

    private void closeStream(ByteArrayOutputStream out, OutputStream out2, BufferedOutputStream bout, HttpURLConnection connection) {
        try {
            if (bout != null) {
                bout.close();
            }
            if (out2 != null) {
                out2.close();
            }
            if (out != null) {
                out.close();
            }
            if (connection != null) {
                connection.disconnect();
            }
        } catch (IOException e) {
            LOGGER.log(Level.SEVERE, "Error closing streams and connections", e);
        }
    }
}
