import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.util.ArrayList;
import java.util.List;
import java.util.function.Consumer;

public class OsmInputFile {
    private final Path path;
    private final boolean lazy;

    public OsmInputFile(Path path, boolean lazy) {
        this.path = path;
        this.lazy = lazy;
    }

    public int readInt(FileChannel channel) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        channel.read(buffer);
        buffer.flip();
        return buffer.getInt();
    }

    public byte[] readBytes(FileChannel channel, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.read(buffer);
        buffer.flip();
        byte[] bytes = new byte[length];
        buffer.get(bytes);
        return bytes;
    }

    public byte[] readBytes(FileChannel channel, long offset, int length) throws IOException {
        channel.position(offset);
        return readBytes(channel, length);
    }

    public BlobHeader readBlobHeader(FileChannel channel) throws IOException {
        int length = readInt(channel);
        byte[] headerBytes = readBytes(channel, length);
        return BlobHeader.parseFrom(headerBytes);
    }

    public FileChannel openChannel() throws IOException {
        return FileChannel.open(path);
    }

    public OsmBlockSource getBlockSource() throws IOException {
        if (lazy) {
            return new LazyReader();
        } else {
            return new EagerReader();
        }
    }

    private class LazyReader implements OsmBlockSource {
        private final FileChannel channel;
        private final long fileSize;

        public LazyReader() throws IOException {
            channel = FileChannel.open(path);
            fileSize = channel.size();
        }

        @Override
        public void forEachBlock(Consumer<Block> consumer) throws IOException {
            long offset = 0;
            while (offset < fileSize) {
                BlobHeader header = readBlobHeader(channel);
                long blockLength = header.getDatasize() + header.getOffset();
                LazyBlock lazyBlock = new LazyBlock(offset, (int) blockLength, channel);
                consumer.accept(lazyBlock);
                offset += blockLength;
            }
        }

        @Override
        public void close() throws IOException {
            channel.close();
        }
    }

    private record LazyBlock(long offset, int length, FileChannel channel) implements Block {
        @Override
        public Iterable<OsmElement> decodeElements() throws IOException {
            byte[] blockBytes = readBytes(channel, offset, length);
            return OsmElementDecoder.decode(blockBytes);
        }
    }
}
