import java.util.concurrent.CompletableFuture;

public final class NettyRpcClient implements RpcRequestTransport {

    private final ServiceDiscovery serviceDiscovery;
    private final UnprocessedRequests unprocessedRequests;
    private final ChannelProvider channelProvider;
    private final Bootstrap bootstrap;
    private final EventLoopGroup eventLoopGroup;

    public NettyRpcClient() {
        this.serviceDiscovery = new ServiceDiscovery();
        this.unprocessedRequests = new UnprocessedRequests();
        this.channelProvider = new ChannelProvider();
        this.bootstrap = new Bootstrap();
        this.eventLoopGroup = new NioEventLoopGroup();
        this.bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .handler(new NettyRpcClientInitializer(unprocessedRequests));
    }

    @Override
    public Channel doConnect(InetSocketAddress inetSocketAddress) {
        return bootstrap.connect(inetSocketAddress).syncUninterruptibly().channel();
    }

    @Override
    public CompletableFuture<Object> sendRpcRequest(RpcRequest rpcRequest) {
        CompletableFuture<Object> completableFuture = new CompletableFuture<>();
        Channel channel = getChannel(rpcRequest.getRpcServiceName());
        if (channel.isActive()) {
            unprocessedRequests.put(rpcRequest.getRequestId(), completableFuture);
            channel.writeAndFlush(rpcRequest);
        } else {
            completableFuture.completeExceptionally(new RuntimeException("Failed to send RPC request: Channel is not active"));
        }
        return completableFuture;
    }

    private Channel getChannel(String rpcServiceName) {
        return channelProvider.acquireChannel(serviceDiscovery.discover(rpcServiceName));
    }

    @Override
    public void close() {
        eventLoopGroup.shutdownGracefully();
    }
