                    } else {
                        logger.error("Failed to connect to proxy server", future.cause());
                        reconnectWait();
                        connectProxyServer();
                    }
                });
    }

    public void stop() {
        workerGroup.shutdownGracefully();
    }

    public void channelInactive(ChannelHandlerContext ctx) {
