        CompletableFuture<RemotingCommand> completableFuture = new CompletableFuture<>();
        channel.writeAndFlush(request).addListener(future -> {
            if (!future.isSuccess()) {
                completableFuture.completeExceptionally(future.cause());
            }
        });

