import java.io.DataOutputStream;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.util.logging.Logger;

public abstract class AppendStoreMmap {
    protected DataOutputStream outputStream;
    protected int segmentBits;
    protected long segmentMask;
    protected long segmentBytes;
    protected Path path;
    protected boolean madvise;
    protected long outIdx;
    protected MappedByteBuffer[] segments;
    protected FileChannel channel;
    protected static final Logger LOGGER = Logger.getLogger(AppendStoreMmap.class.getName());

    public AppendStoreMmap(Path path, boolean madvise) {
        this(path, 1024L * 1024L * 1024L, madvise);
    }

    public AppendStoreMmap(Path path, long segmentSizeBytes, boolean madvise) {
        this.path = path;
        this.madvise = madvise;
        this.segmentBytes = segmentSizeBytes;
        this.segmentBits = 0;
        while (segmentBytes > 0) {
            segmentBytes >>>= 1;
            segmentBits++;
        }
        segmentMask = (1L << segmentBits) - 1;
        try {
            channel = FileChannel.open(path, CREATE, WRITE);
        } catch (IOException e) {
            LOGGER.severe("Failed to open file channel: " + e.getMessage());
        }
    }

    protected MappedByteBuffer[] getSegments() {
        if (segments == null) {
            try {
                long size = channel.size();
                int numSegments = (int) ((size + segmentBytes - 1) >>> segmentBits);
                segments = new MappedByteBuffer[numSegments];
                for (int i = 0; i < numSegments; i++) {
                    long pos = (long) i << segmentBits;
                    long len = Math.min(segmentBytes, size - pos);
                    segments[i] = channel.map(MapMode.READ_WRITE, pos, len);
                }
            } catch (IOException e) {
                LOGGER.severe("Failed to map segments: " + e.getMessage());
            }
        }
        return segments;
    }

    public void close() {
        if (segments != null) {
            for (MappedByteBuffer segment : segments) {
                if (segment != null) {
                    try {
                        segment.force();
                    } catch (IOException e) {
                        LOGGER.severe("Failed to force segment: " + e.getMessage());
                    }
                    segment = null;
                }
            }
            segments = null;
        }
        if (channel != null) {
            try {
                channel.close();
            } catch (IOException e) {
                LOGGER.severe("Failed to close channel: " + e.getMessage());
            }
            channel = null;
        }
    }

    public long diskUsageBytes() {
        try {
            return channel.size();
        } catch (IOException e) {
            LOGGER.severe("Failed to get disk usage: " + e.getMessage());
            return -1;
        }
    }
}
