public class NettyTcpClient {

    private static final InternalLoggerFactory loggerFactory = Log4J2LoggerFactory.getInstance();
    private static final Logger LOG = loggerFactory.getInstance(NettyTcpClient.class);

    private final URI uri;
    private Channel webSocketChannel;
    private EventLoopGroup eventLoopGroup;
    private boolean compressedMessages;
    private boolean acceptAllCertificates;
    private boolean enableLoggingHandler;
    private LogLevel loggingHandlerLevel;
    private String socksProxyHost;
    private Integer socksProxyPort;

    public NettyTcpClient(URI uri) {
        this.uri = uri;
    }

    public void connect() {
        eventLoopGroup = new NioEventLoopGroup();
        try {
            final String scheme = uri.getScheme() == null ? "ws" : uri.getScheme();
            final String host = uri.getHost() == null ? "127.0.0.1" : uri.getHost();
            final int port;
            if (uri.getPort() == -1) {
                if ("ws".equalsIgnoreCase(scheme)) {
                    port = 80;
                } else if ("wss".equalsIgnoreCase(scheme)) {
                    port = 443;
                } else {
                    port = -1;
                }
            } else {
                port = uri.getPort();
            }

            if (!"ws".equalsIgnoreCase(scheme) && !"wss".equalsIgnoreCase(scheme)) {
                throw new IllegalArgumentException("Only WS(S) is supported.");
            }

            final boolean ssl = "wss".equalsIgnoreCase(scheme);
            final SslContext sslCtx;
            if (ssl) {
                if (acceptAllCertificates) {
                    sslCtx = SslContextBuilder.forClient().trustManager(InsecureTrustManagerFactory.INSTANCE).build();
                } else {
                    sslCtx = SslContextBuilder.forClient().build();
                }
            } else {
                sslCtx = null;
            }

            final WebSocketClientHandshaker handshaker = WebSocketClientHandshakerFactory.newHandshaker(
                    uri, WebSocketVersion.V13, null, compressedMessages, new DefaultHttpHeaders());

            final Bootstrap b = new Bootstrap();
            b.group(eventLoopGroup)
                    .channel(NioSocketChannel.class)
                    .option(ChannelOption.TCP_NODELAY, true)
                    .handler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) {
                            if (enableLoggingHandler) {
                                ch.pipeline().addLast(new LoggingHandler(loggingHandlerLevel));
                            }
                            if (ssl) {
                                ch.pipeline().addLast(sslCtx.newHandler(ch.alloc(), host, port));
