                            ch.pipeline().addLast(
                                    new HttpClientCodec(),
                                    new HttpObjectAggregator(8192),
                                    getWebSocketClientExtensionHandler(),
                                    getWebSocketClientHandler(handshaker, new WebSocketClientHandler.WebSocketMessageHandler() {
                                        @Override
                                        public void onTextMessage(String message) {
                                            LOG.info("Received text message: {}", message);
                                        }

                                        @Override
                                        public void onClose(int statusCode, String reason) {
                                            LOG.info("Connection closed: {} - {}", statusCode, reason);
                                        }
                                    }));
                        }
                    });

            final ChannelFuture connectFuture = b.connect(host, port);
            connectFuture.addListener(new GenericFutureListener<Future<? super Void>>() {
                @Override
                public void operationComplete(Future<? super Void> future) throws Exception {
                    if (future.isSuccess()) {
                        webSocketChannel = connectFuture.channel();
                        webSocketChannel.closeFuture().addListener(new GenericFutureListener<Future<? super Void>>() {
                            @Override
                            public void operationComplete(Future<? super Void> future) throws Exception {
                                eventLoopGroup.shutdownGracefully();
                            }
                        });
                        handshaker.handshake(webSocketChannel).addListener(new GenericFutureListener<Future<? super Void>>() {
                            @Override
                            public void operationComplete(Future<? super Void> future) throws Exception {
                                if (future.isSuccess()) {
                                    LOG.info("WebSocket client connected successfully.");
                                } else {
                                    LOG.error("WebSocket client connection failed.", future.cause());
                                }
                            }
                        });
                    } else {
                        LOG.error("WebSocket client connection failed.", future.cause());
                    }
                }
            });
        } catch (Exception e) {
            LOG.error("WebSocket client connection failed.", e);
        }
    }

    public void disconnect() {
        if (webSocketChannel != null) {
            webSocketChannel.close();
        }
    }

    private WebSocketClientExtensionHandler getWebSocketClientExtensionHandler() {
        if (compressedMessages) {
            return WebSocketClientCompressionHandler.INSTANCE;
        } else {
            return null;
        }
    }

    private WebSocketClientHandler getWebSocketClientHandler(WebSocketClientHandshaker handshaker, WebSocketClientHandler.WebSocketMessageHandler messageHandler) {
        return new WebSocketClientHandler(handshaker, messageHandler);
    }
}
