                if (line == null || "bye".equalsIgnoreCase(line)) {
                    ch.closeFuture().sync();
                    break;
                }
                ch.writeAndFlush(line + "\r\n");
            }
        } finally {
            group.shutdownGracefully(0, 5, TimeUnit.SECONDS);
        }
    }

