import io.netty.bootstrap.Bootstrap;
import io.netty.channel.Channel;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;
import io.netty.handler.timeout.ReadTimeoutHandler;

import javax.net.ssl.SSLParameters;
import java.util.concurrent.TimeUnit;

public class NettyTcpClient {

    private Bootstrap bootstrap;
    private EventLoopGroup eventLoopGroup;
    private int connectTimeout;
    private int readTimeout;
    private String threadPrefix;

    public void init(DockerClientConfig dockerClientConfig) {
        this.connectTimeout = dockerClientConfig.getConnectTimeout();
        this.readTimeout = dockerClientConfig.getReadTimeout();
        this.threadPrefix = dockerClientConfig.getThreadPrefix();

        this.bootstrap = new Bootstrap();
        this.eventLoopGroup = new NioEventLoopGroup(1, new ThreadFactoryBuilder().setNameFormat(threadPrefix + "-%d").build());
        this.bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ch.pipeline().addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
                    }
                });
    }

    public DuplexChannel connect(DockerClientConfig dockerClientConfig) {
        SslHandler sslHandler = initSsl(dockerClientConfig);
        Channel channel = bootstrap.connect(dockerClientConfig.getDaemonHost(), dockerClientConfig.getDaemonPort()).syncUninterruptibly().channel();
        channel.pipeline().addFirst(sslHandler);
        return new DuplexChannel(channel);
    }

    private SslHandler initSsl(DockerClientConfig dockerClientConfig) {
        SslContext sslContext = SslContext.newClientContext(InsecureTrustManagerFactory.INSTANCE);
        SSLParameters sslParameters = enableHostNameVerification(dockerClientConfig.getSslParameters());
        return sslContext.newHandler(channel.alloc(), dockerClientConfig.getDaemonHost(), dockerClientConfig.getDaemonPort(), sslParameters);
    }

    private SSLParameters enableHostNameVerification(SSLParameters sslParameters) {
        sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
        return sslParameters;
    }

    public void close() {
        eventLoopGroup.shutdownGracefully();
    }

    private <T extends Channel> T configure(T channel) {
        channel.config().setConnectTimeoutMillis(connectTimeout);
        return channel;
    }

    private static class ReadTimeoutHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            if (evt instanceof ReadTimeoutException) {
                ctx.close();
            } else {
                super.userEventTriggered(ctx, evt);
            }
        }
    }
}
