            private final String prefix = "NettyHttpServerBoss_" + port;
            private final AtomicInteger nextId = new AtomicInteger(0);

            @Override
            public Thread newThread(Runnable r) {
                Thread t = new Thread(r, prefix + nextId.getAndIncrement());
                t.setDaemon(true);
                return t;
            }
        });

        EventLoopGroup workerGroup = new NioEventLoopGroup(workerThreadNum, new ThreadFactory() {
            private final AtomicInteger threadIndex = new AtomicInteger(0);
            private final String prefix = "NettyHttpServerWorker_" + port;
