                ch.pipeline().addLast(new IdleStateHandler(0, 0, 30));
                ch.pipeline().addLast(new MysqlHandshakeHandler());
                ch.pipeline().addLast(new MysqlMessageDecoder());
                ch.pipeline().addLast(new MysqlCommandHandler());
                ch.pipeline().addLast(new MysqlExceptionHandler());
            }
        };
    }

    public void close() {
        eventLoopGroup.shutdownGracefully();
        connections.values().forEach(MysqlConnection::close);
    }
}
