            @Override
            protected void initChannel(SocketChannel ch) throws Exception {
                setupHandlers(ch, createRequestDispatcher());
            }
        };
    }

    private void setupHandlers(SocketChannel ch, RequestDispatcher dispatcher) {
        ch.pipeline().addLast("decoder", new HttpRequestDecoder());
        ch.pipeline().addLast("encoder", new HttpResponseEncoder());
        ch.pipeline().addLast("aggregator", new HttpObjectAggregator(maxRequestSize));
        ch.pipeline().addLast("handler", new SofaNettyJaxrsHandler(dispatcher, httpChannelHandlers));
    }

    private RequestDispatcher createRequestDispatcher() {
        deployment.setSecurityDomain(domain);
        deployment.setApplicationClass(root);
        return deployment.createRequestDispatcher();
    }

    private class SslChannelInitializer extends ChannelInitializer<SocketChannel> {
        private final ChannelInitializer<SocketChannel> nextInit;

        public SslChannelInitializer(ChannelInitializer<SocketChannel> nextInit) {
            this.nextInit = nextInit;
        }

        @Override
        protected void initChannel(SocketChannel ch) throws Exception {
            ch.pipeline().addLast("ssl", sslContext.newHandler(ch.alloc()));
            nextInit.initChannel(ch);
        }
    }
