    return promise.future();
  }

  @Override
  public AsyncFileImpl read(Buffer buffer, int offset, long position, int length) {
    check();
    if (length == -1) {
      length = buffer.length() - offset;
    }
    if (length == 0) {
      context.runOnContext(v -> handler.handle(buffer));
      return this;
    }
    ByteBuffer buff = buffer.getByteBuf().nioBuffer(offset, length);
    Promise<Buffer> promise = Promise.promise();
    doRead(buffer, offset, buff, position, promise);
    promise.future().onComplete(ar -> {
