  }

  private void doWrite(Buffer buffer, long position, Handler<AsyncResult<Void>> handler) {
    check();
    if (buffer.length() == 0) {
      if (handler != null) {
        handler.handle(Future.succeededFuture());
      }
      return;
    }
    ByteBuffer[] buffers = new ByteBuffer[buffer.length()];
    for (int i = 0; i < buffer.length(); i++) {
      buffers[i] = buffer.getByteBuf().nioBuffer(i);
    }
    doWrite(buffers, position, handler);
  }

  private void doWrite(ByteBuffer[] buffers, long position, Handler<AsyncResult<Void>> handler) {
    check();
    ch.write(buffers, position, null, new CompletionHandler<Long, Void>() {
      @Override
      public void completed(Long result, Void attachment) {
        if (handler != null) {
          handler.handle(Future.succeededFuture());
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        if (handler != null) {
          handler.handle(Future.failedFuture(exc));
