  private void doWrite(ByteBuffer buff, long position, long toWrite, Handler<AsyncResult<Void>> handler) {
    check();
    ch.write(buff, position, toWrite, null, new CompletionHandler<Long, Void>() {
      @Override
      public void completed(Long result, Void attachment) {
        if (handler != null) {
          handler.handle(Future.succeededFuture());
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        if (handler != null) {
          handler.handle(Future.failedFuture(exc));
        }
      }
    });
  }

  private void writeInternal(ByteBuffer buff, long position, Handler<AsyncResult<Void>> handler) {
    check();
    long toWrite = buff.remaining();
    if (toWrite == 0) {
      if (handler != null) {
        handler.handle(Future.succeededFuture());
      }
      return;
    }
    doWrite(buff, position, toWrite, handler);
  }

  private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
    check();
    ch.read(buff, position, null, new CompletionHandler<Integer, Void>() {
      @Override
      public void completed(Integer result, Void attachment) {
        if (result == -1) {
          promise.complete(null);
        } else {
          writeBuff.getByteBuf().setBytes(offset, buff);
          if (result < buff.capacity()) {
            promise.complete(writeBuff);
          } else {
            doRead(writeBuff, offset, buff, position + result, promise);
          }
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        promise.fail(exc);
      }
    });
  }

  private void check() {
    if (closed) {
      throw new IllegalStateException("File is closed");
    }
  }

  private void closeInternal(Promise<Void> handler) {
    check();
    closed = true;
    ch.close(ar -> {
      if (ar.succeeded()) {
        handler.complete();
      } else {
        handler.fail(ar.cause());
      }
    });
  }
}
