import io.netty.handler.codec.http.FullHttpResponse;
import io.netty.handler.codec.http.HttpContent;
import io.netty.handler.codec.http.HttpHeaderNames;
import io.netty.handler.codec.http.HttpResponseStatus;
import io.netty.handler.codec.http.HttpServerCodec;
import io.netty.handler.codec.http.HttpVersion;
import io.netty.handler.codec.http.LastHttpContent;
import java.util.Map;

public class NettyTcpServer {

    private ApplicationContext ctx;
    private EventLoopGroup serverLoop;
    private ServerSocketChannel server;

    public void startServer(int port) throws Exception {
        ctx = ApplicationContext.run(Map.of("spec.name", "NettyTcpServer"));
        serverLoop = new NioEventLoopGroup(1);
        server = (ServerSocketChannel) new ServerBootstrap()
            .group(serverLoop)
            .channel(NioServerSocketChannel.class)
            .localAddress(port)
            .childHandler(new ChannelInitializer<>() {
                FullHttpResponse response;

                @Override
                public void handlerAdded(ChannelHandlerContext ctx) {
                    ByteBuf resp = ctx.alloc().buffer();
                    ByteBufUtil.writeAscii(resp, "bar");
                    response = new DefaultFullHttpResponse(
                        HttpVersion.HTTP_1_1, 
                        HttpResponseStatus.OK, 
                        resp, 
                        new DefaultHttpHeaders().add(HttpHeaderNames.CONTENT_LENGTH, resp.readableBytes()), 
                        EmptyHttpHeaders.INSTANCE
                    );
                }

                @Override
                protected void initChannel(@NonNull Channel ch) {
                    ch.pipeline()
                        .addLast(new HttpServerCodec())
                        .addLast(new ChannelInboundHandlerAdapter() {
                            boolean inBody = false;

                            @Override
                            public void channelRead(@NonNull ChannelHandlerContext ctx, @NonNull Object msg) throws Exception {
                                if (!inBody) {
                                    inBody = true;
                                    if (!(msg instanceof FullHttpResponse)) {
                                        return;
                                    }
                                }
                                ((HttpContent) msg).release();
                                if (msg instanceof LastHttpContent) {
                                    ctx.writeAndFlush(new DefaultFullHttpResponse(
                                        response.protocolVersion(),
                                        response.status(),
                                        response.content().retainedSlice(),
                                        response.headers(),
                                        response.trailingHeaders()
                                    ));
                                    inBody = false;
                                }
                            }
                        });
                }
            })
            .bind().syncUninterruptibly().channel();
    }

    public void stopServer() {
        if (serverLoop != null) {
            serverLoop.shutdownGracefully();
        }
        if (ctx != null) {
            ctx.close();
        }
    }

    public int getPort() {
        return server != null ? server.localAddress().getPort() : -1;
    }
}

