        this.eventExecutorGroup = new DefaultEventExecutorGroup(config.getBusinessThreads());
    }

    public ChannelFuture start() {
        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(acceptorEventLoopGroup, networkEventLoopGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ch.pipeline().addLast(eventExecutorGroup, new ProtocolServerHandler(applicationContext));
                    }
                })
                .option(ChannelOption.SO_BACKLOG, config.getBacklog())
                .childOption(ChannelOption.SO_KEEPALIVE, true);

        return bootstrap.bind(config.getPort());
    }

    public void shutdownGracefully() {
        acceptorEventLoopGroup.shutdownGracefully();
        networkEventLoopGroup.shutdownGracefully();
        eventExecutorGroup.shutdownGracefully();
    }
}
