import java.io.File;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.StandardOpenOption;

public class FileChannelInteractor {
    private FileChannel fileChannel;
    private long mapAddress;
    private long fileSize;

    public FileChannelInteractor(String filePath) throws IOException {
        File file = new File(filePath);
        fileChannel = FileChannel.open(file.toPath(), StandardOpenOption.READ);
        fileSize = fileChannel.size();
        MappedByteBuffer buffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, fileSize);
        mapAddress = buffer.address();
    }

    public long getMemoryMappedAddress() {
        return mapAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

    public void close() throws IOException {
        fileChannel.close();
    }

    public static void spawnWorkerProcess() throws IOException, InterruptedException {
        String[] cmd = System.getProperty("sun.java.command").split(" ");
        String[] workerCmd = new String[cmd.length + 1];
        System.arraycopy(cmd, 0, workerCmd, 0, cmd.length);
        workerCmd[cmd.length] = "--worker";
        ProcessBuilder pb = new ProcessBuilder(workerCmd);
        pb.inheritIO();
        Process process = pb.start();
        process.waitFor();
    }
}
