import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.ChannelOption;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;

import java.util.HashMap;
import java.util.Map;

public class Console {
    private static Console instance;
    private Map<String, Class> commands;

    private Console() {
        commands = new HashMap<>();
        registerDefaultCommands();
    }

    public static Console getInstance() {
        if (instance == null) {
            instance = new Console();
        }
        return instance;
    }

    public void listenOnTCP(int port) {
        EventLoopGroup bossGroup = new NioEventLoopGroup();
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        public void initChannel(SocketChannel ch) throws Exception {
                            ch.pipeline().addLast(new StringDecoder(), new StringEncoder(), new Adapter());
                        }
                    })
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            e.printStackTrace();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private void registerDefaultCommands() {
        commands.put("help", HelpCommand.class);
        commands.put("hello", HelloCommand.class);
        commands.put("device", DeviceCommand.class);
    }

    private class Adapter extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            String command = (String) msg;
            if (commands.containsKey(command)) {
                try {
                    Command cmd = (Command) commands.get(command).newInstance();
                    String response = cmd.execute();
                    sendString(ctx, response);
                } catch (InstantiationException | IllegalAccessException e) {
                    e.printStackTrace();
                }
            } else {
                sendString(ctx, "Unknown command");
            }
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            sendPrompt(ctx);
        }

        @Override
        public void channelReadComplete(ChannelHandlerContext ctx) {
            ctx.flush();
        }

        private void sendStringL(ChannelHandlerContext ctx, String text) {
            if (!text.endsWith("\n")) {
                text += "\n";
            }
            sendString(ctx, text);
        }

        private void sendString(ChannelHandlerContext ctx, String text) {
            ctx.writeAndFlush(text);
        }

        private void sendPrompt(ChannelHandlerContext ctx) {
            sendStringL(ctx, "> ");
        }
    }

    private abstract static class Command {
        protected String command;

        Command(String command) {
            this.command = command;
        }

        abstract String execute();
    }

    private static class HelpCommand extends Command {
        HelpCommand(String command) {
            super(command);
        }

        @Override
        String execute() {
            return "Available commands: help, hello, device";
        }
    }

    private static class HelloCommand extends Command {
        HelloCommand(String command) {
            super(command);
        }

        @Override
        String execute() {
            String[] greetings = {"Hello", "Hi", "Hey", "Greetings"};
            int index = (int) (Math.random() * greetings.length);
            return greetings[index];
        }
    }

    private static class DeviceCommand extends Command {
        DeviceCommand(String command) {
            super(command);
        }

        @Override
        String execute() {
            return "{\"device\":\"Netty TCP Server\",\"version\":\"1.0\"}";
        }
    }
}
