        this.messageHandler = messageHandler;
        this.rejectCounter = new AtomicInteger(0);
        this.channelManage = new NettyServerChannelManage();
    }

    @Override
    public boolean open() {
        bossGroup = new NioEventLoopGroup(1, new DefaultThreadFactory("NettyServerBoss", true));
        workerGroup = new NioEventLoopGroup(url.getPositiveIntParameter(URLParamEnum.THREADS.getValue(), 200),
                new DefaultThreadFactory("NettyServerWorker", true));
        standardThreadExecutor = new StandardThreadExecutor(url.getPositiveIntParameter(URLParamEnum.THREADS.getValue(), 200),
                url.getPositiveIntParameter(URLParamEnum.QUEUES.getValue(), 0),
                60L, TimeUnit.SECONDS, new DefaultThreadFactory("NettyServerStandard", true));
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .option(ChannelOption.SO_BACKLOG, 1024)
                    .handler(new LoggingHandler(LogLevel.INFO))
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        public void initChannel(SocketChannel ch) throws Exception {
                            ch.pipeline().addLast(new NettyServerHandler(messageHandler, channelManage, standardThreadExecutor));
                        }
                    });
            ChannelFuture f = b.bind(url.getPort()).sync();
            serverChannel = f.channel();
            return true;
        } catch (InterruptedException e) {
            return false;
        }
    }

    @Override
    public void close() {
        if (serverChannel != null) {
            serverChannel.close();
        }
        if (workerGroup != null) {
            workerGroup.shutdownGracefully();
        }
        if (bossGroup != null) {
            bossGroup.shutdownGracefully();
        }
        if (standardThreadExecutor != null) {
            standardThreadExecutor.shutdown();
        }
        channelManage.close();
        setState(State.CLOSE);
    }

    @Override
    public boolean isBound() {
        return serverChannel != null && serverChannel.isActive();
    }

