    private static final JsonFactory JSON_FACTORY = new JsonFactory(new ObjectMapper());

    public HttpTestServer(int port, boolean fail, BiConsumer<HttpRequest, JsonGenerator> requestHandler, Map<String, String> headers) {
        this.port = port;
        this.fail = fail;
        this.requestHandler = requestHandler;
        this.headers = headers;
    }

    public HttpTestServer(int port, boolean fail, BiConsumer<HttpRequest, JsonGenerator> requestHandler) {
        this(port, fail, requestHandler, null);
    }

    public void run() throws InterruptedException {
        group = new NioEventLoopGroup();
        ServerBootstrap b = new ServerBootstrap();
        b.group(group)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    public void initChannel(SocketChannel ch) throws Exception {
                        ch.pipeline().addLast("http-codec", new HttpServerCodec());
                        ch.pipeline().addLast("aggregator", new HttpObjectAggregator(65536));
                        ch.pipeline().addLast("http-chunked", new ChunkedWriteHandler());
                        ch.pipeline().addLast("handler", new HttpTestServerHandler());
                    }
                })
                .option(ChannelOption.SO_BACKLOG, 128)
                .childOption(ChannelOption.SO_KEEPALIVE, true);

        ChannelFuture f = b.bind(port).sync();
        channel = f.channel();
    }

    public int boundPort() {
        return channel.localAddress().hashCode();
    }

    public void shutDown() throws InterruptedException {
        channel.closeFuture().sync();
        group.shutdownGracefully();
    }

    private class HttpTestServerHandler extends io.netty.channel.SimpleChannelInboundHandler<io.netty.handler.codec.http.HttpObject> {
        @Override
        protected void channelRead0(io.netty.channel.ChannelHandlerContext ctx, io.netty.handler.codec.http.HttpObject msg) throws Exception {
            if (msg instanceof io.netty.handler.codec.http.HttpRequest) {
                io.netty.handler.codec.http.HttpRequest request = (io.netty.handler.codec.http.HttpRequest) msg;
                try (JsonGenerator generator = JSON_FACTORY.createGenerator(responses::add)) {
                    ObjectNode rootNode = new ObjectMapper().createObjectNode();
                    requestHandler.accept(request, generator);
                    io.netty.handler.codec.http.HttpResponse response = new io.netty.handler.codec.http.DefaultFullHttpResponse(io.netty.handler.codec.http.HttpVersion.HTTP_1_1, fail ? HttpResponseStatus.INTERNAL_SERVER_ERROR : HttpResponseStatus.OK, io.netty.buffer.Unpooled.wrappedBuffer(rootNode.toString().getBytes()));
                    if (headers != null) {
                        headers.forEach((key, value) -> response.headers().set(key, value));
                    }
                    ctx.writeAndFlush(response);
                }
            }
        }
    }
}
