public class NettyHttpServer {

    private final EventLoopGroup bossGroup;
    private final EventLoopGroup workerGroup;
    private final EventExecutorGroup tracingGroup;

    public NettyHttpServer() {
        bossGroup = new NioEventLoopGroup(1);
        workerGroup = new NioEventLoopGroup();
        tracingGroup = new DefaultEventExecutorGroup(4);
    }

    public int startServer() throws InterruptedException {
        ServerBootstrap b = new ServerBootstrap();
        b.group(bossGroup, workerGroup)
                .channel(NioServerSocketChannel.class)
                .handler(new LoggingHandler(LogLevel.INFO))
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    public void initChannel(SocketChannel ch) throws Exception {
                        ch.pipeline().addLast(new HttpServerCodec());
                        ch.pipeline().addLast(new HttpServerExpectContinueHandler());
                        ch.pipeline().addLast(new HttpObjectAggregator(65536));
                        ch.pipeline().addLast(new TracingDispatchHandler());
                    }
                })
                .childOption(ChannelOption.SO_KEEPALIVE, true);

        ChannelFuture f = b.bind(0).sync();
        return ((InetSocketAddress) f.channel().localAddress()).getPort();
    }

    public void stopServer() throws InterruptedException {
        bossGroup.shutdownGracefully();
        workerGroup.shutdownGracefully();
        tracingGroup.shutdownGracefully();
    }

    private class TracingDispatchHandler extends SimpleChannelInboundHandler<Object> {

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, Object msg) throws Exception {
            if (msg instanceof HttpRequest) {
                HttpRequest request = (HttpRequest) msg;
                String uri = request.uri();
                if ("/unsampled".equals(uri)) {
                    ctx.pipeline().addLast(tracingGroup, new UnsampledTracingHandler());
                } else if ("/traced".equals(uri)) {
                    ctx.pipeline().addLast(tracingGroup, new TracedHandler());
                }
            }
            ctx.fireChannelRead(msg);
        }

        @Override
        public void write(ChannelHandlerContext ctx, Object msg, ChannelPromise promise) throws Exception {
            if (msg instanceof HttpResponse) {
                HttpResponse response = (HttpResponse) msg;
                if (response.headers().contains("X-Trace-Id")) {
                    ctx.pipeline().addLast(tracingGroup, new TracedHandler());
                }
            }
            super.write(ctx, msg, promise);
        }
    }
}
