public class GeyserServer {
    private static final Transport TRANSPORT = compatibleTransport();
    private static final int SHUTDOWN_QUIET_PERIOD_MS = 500;
    private static final int SHUTDOWN_TIMEOUT_MS = 3000;
    private final GeyserImpl geyser;
    private final EventLoopGroup group;
    private final EventLoopGroup childGroup;
    private final ServerBootstrap bootstrap;
    private final EventLoopGroup playerGroup;
    private final ChannelFuture[] bootstrapFutures;

    public GeyserServer(GeyserImpl geyser, int threadCount) {
        this.geyser = geyser;
        this.group = TRANSPORT.eventLoopGroupFactory.newEventLoopGroup(threadCount);
        this.childGroup = TRANSPORT.eventLoopGroupFactory.newEventLoopGroup(threadCount);
        this.bootstrap = createBootstrap();
        this.playerGroup = TRANSPORT.eventLoopGroupFactory.newEventLoopGroup(threadCount);
        this.bootstrapFutures = new ChannelFuture[1];
    }

    public CompletableFuture<Void> bind(InetSocketAddress address) {
        return CompletableFuture.runAsync(() -> {
            bootstrapFutures[0] = bootstrap.bind(address);
            bootstrapFutures[0].syncUninterruptibly();
        });
    }

    public void shutdown() {
        group.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS);
        childGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS);
        playerGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS);
        for (ChannelFuture future : bootstrapFutures) {
            future.channel().close().syncUninterruptibly();
        }
    }

    private ServerBootstrap createBootstrap() {
        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(group, childGroup)
                .channel(TRANSPORT.channelClass)
                .childHandler(new GeyserServerInitializer(geyser, playerGroup));
        return bootstrap;
    }

    private static Transport compatibleTransport() {
        if (isClassAvailable("io.netty.channel.epoll.EpollDatagramChannel")) {
            return Transport.EPOLL;
        } else if (isClassAvailable("io.netty.channel.kqueue.KQueueDatagramChannel")) {
            return Transport.KQUEUE;
        } else if (isClassAvailable("io.netty.channel.socketio.IOUringDatagramChannel")) {
            return Transport.IO_URING;
        } else {
            return Transport.NIO;
        }
    }

    private static boolean isClassAvailable(String className) {
        try {
            Class.forName(className);
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

    public record Transport(Class<? extends io.netty.channel.Channel> channelClass, EventLoopGroupFactory eventLoopGroupFactory) {
        public static final Transport NIO = new Transport(NioDatagramChannel.class, NioEventLoopGroup::new);
        public static final Transport EPOLL = new Transport(EpollDatagramChannel.class, Epoll::newEventLoopGroup);
        public static final Transport KQUEUE = new Transport(KQueueDatagramChannel.class, KQueue::newEventLoopGroup);
        public static final Transport IO_URING = new Transport(IOUringDatagramChannel.class, IOUringEventLoopGroup::new);
    }
