import io.netty.bootstrap.Bootstrap;
import io.netty.channel.Channel;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.ChannelOption;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.DatagramChannel;
import io.netty.channel.socket.nio.NioDatagramChannel;
import io.netty.handler.codec.DatagramPacketDecoder;
import io.netty.handler.codec.DatagramPacketEncoder;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class DatagramChannelEchoClient {

    private final String host;
    private final int port;
    private EventLoopGroup group;
    private Channel channel;

    public DatagramChannelEchoClient(String host, int port) {
        this.host = host;
        this.port = port;
    }

    public void start() throws InterruptedException {
        group = new NioEventLoopGroup();
        try {
            Bootstrap b = new Bootstrap();
            b.group(group)
                    .channel(NioDatagramChannel.class)
                    .option(ChannelOption.SO_BROADCAST, true)
                    .handler(new ChannelInitializer<DatagramChannel>() {
                        @Override
                        protected void initChannel(DatagramChannel ch) throws Exception {
                            ch.pipeline().addLast(new StringDecoder(), new StringEncoder(),
                                    new DatagramPacketDecoder(new StringDecoder()), new DatagramPacketEncoder(new StringEncoder()));
                        }
                    });

            ChannelFuture f = b.bind(port).sync();
            channel = f.channel();
        } catch (InterruptedException e) {
            throw e;
        }
    }

    public void sendMessage(String message) {
        channel.writeAndFlush(new DatagramPacket(message.getBytes(), new InetSocketAddress(host, port)));
    }

    public void stop() {
        if (channel != null) {
            channel.close();
        }
        if (group != null) {
            group.shutdownGracefully();
        }
    }

    public void runConsoleInput() throws Exception {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = in.readLine()) != null) {
            sendMessage(line);
        }
    }

    public static void main(String[] args) throws Exception {
        DatagramChannelEchoClient client = new DatagramChannelEchoClient("localhost", 8080);
        client.start();
        client.runConsoleInput();
        client.stop();
    }
}
