import okhttp3.*;
import org.json.JSONObject;

import java.io.IOException;

public class GithubProvider {

    private String clientId;
    private String clientSecret;
    private String redirectUri;

    public GithubProvider(String clientId, String clientSecret, String redirectUri) {
        this.clientId = clientId;
        this.clientSecret = clientSecret;
        this.redirectUri = redirectUri;
    }

    public String getAccessToken(AccessTokenDTO accessTokenDTO) throws IOException {
        OkHttpClient client = new OkHttpClient();

        RequestBody body = new FormBody.Builder()
                .add("client_id", clientId)
                .add("client_secret", clientSecret)
                .add("code", accessTokenDTO.getCode())
                .add("redirect_uri", redirectUri)
                .build();

        Request request = new Request.Builder()
                .url("https://github.com/login/oauth/access_token")
                .post(body)
                .build();

        Response response = client.newCall(request).execute();
        String responseBody = response.body().string();

        String[] split = responseBody.split("&");
        String[] token = split[0].split("=");

        return token[1];
    }

    public GithubUser getUser(String accessToken) throws IOException {
        OkHttpClient client = new OkHttpClient();

        Request request = new Request.Builder()
                .url("https://api.github.com/user")
                .header("Authorization", "token " + accessToken)
                .build();

        Response response = client.newCall(request).execute();
        String responseBody = response.body().string();

        JSONObject jsonObject = new JSONObject(responseBody);

        return new GithubUser(
                jsonObject.getString("id"),
                jsonObject.getString("login"),
                jsonObject.getString("name"),
                jsonObject.getString("avatar_url")
        );
    }
}
