import java.util.List;
import java.util.concurrent.TimeUnit;

public class Scraper {
    private OkHttpClient client;

    public Scraper() {
        this.client = new OkHttpClient.Builder()
                .connectTimeout(10, TimeUnit.SECONDS)
                .readTimeout(10, TimeUnit.SECONDS)
                .build();
    }

    public List<String> scrape(String url, long timeoutMillis) throws IOException {
        Request request = new Request.Builder()
                .url(url)
                .build();

        return executeRequest(request, timeoutMillis);
    }

    public List<String> scrape(String url, String user, String password, long timeoutMillis) throws IOException {
        String credential = Credentials.basic(user, password);
        Request request = new Request.Builder()
                .url(url)
                .header("Authorization", credential)
                .build();

        return executeRequest(request, timeoutMillis);
    }

    private List<String> executeRequest(Request request, long timeoutMillis) throws IOException {
        List<String> result = new ArrayList<>();
        int retryCount = 3;

        while (retryCount > 0) {
            try {
                Response response = client.newCall(request).execute();
                if (response.isSuccessful()) {
                    result.add(response.body().string());
                    return result;
                } else {
                    throw new IOException("Request failed: " + response);
                }
            } catch (IOException e) {
                retryCount--;
                if (retryCount == 0) {
                    throw e;
                }
            }
        }

        return result;
    }
}
