public class DlqAction implements RuleAction {
    private static final Logger log = LoggerFactory.getLogger(DlqAction.class);
    private static final String TYPE = "DLQ";
    private static final String DLQ_TOPIC = "dlq.topic";
    private static final String DLQ_AUTO_FLUSH = "dlq.auto.flush";
    private static final String PRODUCER = "dlq.producer";
    private static final String HEADER_PREFIX = "rule.";
    private static final String RULE_NAME = HEADER_PREFIX + "name";
    private static final String RULE_MODE = HEADER_PREFIX + "mode";
    private static final String RULE_SUBJECT = HEADER_PREFIX + "subject";
    private static final String RULE_TOPIC = HEADER_PREFIX + "topic";
    private static final String RULE_EXCEPTION = HEADER_PREFIX + "exception";

    private Map<String, ?> configs;
    private String topic;
    private boolean autoFlush;
    private KafkaProducer<byte[], byte[]> producer;

    @Override
    public void configure(Map<String, ?> configs) {
        this.configs = configs;
        this.topic = (String) configs.get(DLQ_TOPIC);
        this.autoFlush = Boolean.parseBoolean((String) configs.getOrDefault(DLQ_AUTO_FLUSH, "true"));
        this.producer = producer();
    }

    @Override
    public void run(RuleContext ctx, Object message, RuleException ex) {
        try {
            byte[] bytes = convertToBytes(ctx, message);
            ProducerRecord<byte[], byte[]> producerRecord = new ProducerRecord<>(topic, bytes);
            populateHeaders(ctx, producerRecord, ex);
            producer.send(producerRecord).get();
            if (autoFlush) {
                producer.flush();
            }
        } catch (InterruptedException | ExecutionException e) {
            log.error("Failed to send message to DLQ", e);
        }
    }

    private KafkaProducer<byte[], byte[]> producer() {
        if (producer == null) {
            Properties props = new Properties();
            props.putAll(baseProducerConfigs());
            props.putAll((Map) configs.getOrDefault(PRODUCER, new HashMap<>()));
            producer = new KafkaProducer<>(props, new ByteArraySerializer(), new ByteArraySerializer());
        }
        return producer;
    }

    private byte[] convertToBytes(RuleContext ctx, Object message) {
        if (message instanceof byte[]) {
            return (byte[]) message;
        } else if (message instanceof String) {
            return toBytes((String) message);
        } else {
            return convertToJsonBytes(ctx, message);
        }
    }

    private byte[] convertToJsonBytes(RuleContext ctx, Object message) {
        // Implementation for converting object to JSON byte array
    }

    private void populateHeaders(RuleContext ctx, ProducerRecord<byte[], byte[]> producerRecord, RuleException ex) {
        producerRecord.headers().add(RULE_NAME, toBytes(ctx.getRule().getName()));
        producerRecord.headers().add(RULE_MODE, toBytes(ctx.getRule().getMode().name()));
        producerRecord.headers().add(RULE_SUBJECT, toBytes(ctx.getRule().getSubject()));
        producerRecord.headers().add(RULE_TOPIC, toBytes(ctx.getRule().getTopic()));
        producerRecord.headers().add(RULE_EXCEPTION, toBytes(ex.getMessage()));
    }

    private byte[] toBytes(String value) {
        return value.getBytes(StandardCharsets.UTF_8);
    }

    private Map<String, Object> baseProducerConfigs() {
        Map<String, Object> props = new HashMap<>();
        props.put(ProducerConfig.BOOTSTRAP_SERVERS_CONFIG, "localhost:9092");
        props.put(ProducerConfig.KEY_SERIALIZER_CLASS_CONFIG, ByteArraySerializer.class.getName());
        props.put(ProducerConfig.VALUE_SERIALIZER_CLASS_CONFIG, ByteArraySerializer.class.getName());
        return props;
    }

    @Override
    public void close() {
        if (producer != null) {
            producer.close();
        }
    }
}
