import java.io.IOException;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.atomic.AtomicLong;

public class NIOConnector extends Thread {
    private String name;
    private Selector selector;
    private BlockingQueue<BackendConnection> connectQueue;
    private NIOProcessor[] processors;
    private int nextProcessor;
    private long connectCount;

    public NIOConnector(String name) throws IOException {
        this.name = name;
        this.selector = Selector.open();
        this.connectQueue = new LinkedBlockingQueue<>();
    }

    public void setProcessors(NIOProcessor[] processors) {
        this.processors = processors;
    }

    public void postConnect(BackendConnection c) {
        try {
            connectQueue.put(c);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
        selector.wakeup();
    }

    @Override
    public void run() {
        while (true) {
            try {
                connect(selector);
                selector.select();
                processSelectedKeys();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void connect(Selector selector) throws IOException {
        BackendConnection c = connectQueue.poll();
        if (c == null) {
            return;
        }
        SocketChannel channel = c.getChannel();
        channel.register(selector, SelectionKey.OP_CONNECT, c);
        channel.connect(c.getAddress());
        connectCount++;
    }

    private void processSelectedKeys() throws IOException {
        for (SelectionKey key : selector.selectedKeys()) {
            if (!key.isValid()) {
                continue;
            }
            if (key.isConnectable()) {
                finishConnect(key, key.attachment());
            } else {
                clearSelectionKey(key);
            }
        }
        selector.selectedKeys().clear();
    }

    private void finishConnect(SelectionKey key, Object att) throws IOException {
        BackendConnection c = (BackendConnection) att;
        SocketChannel channel = c.getChannel();
        if (channel.finishConnect()) {
            c.setConnected(true);
            NIOProcessor processor = nextProcessor();
            processor.addConnection(c);
        } else {
            clearSelectionKey(key);
        }
    }

    private void clearSelectionKey(SelectionKey key) {
        key.cancel();
        try {
            key.channel().close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private NIOProcessor nextProcessor() {
        if (processors == null) {
            return null;
        }
        int i = nextProcessor++;
        if (nextProcessor >= processors.length) {
            nextProcessor = 0;
        }
        return processors[i];
    }

    private static class ConnectIdGenerator {
        private static final AtomicLong counter = new AtomicLong(0);

        public static long nextId() {
            return counter.incrementAndGet();
        }
    }
}
