import java.io.File;
import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.function.Consumer;

public class RandomAccessReadMemoryMappedFile {
    private ByteBuffer mappedByteBuffer;
    private long size;
    private FileChannel fileChannel;
    private Consumer<? super ByteBuffer> unmapper;

    public RandomAccessReadMemoryMappedFile(String filename) throws IOException {
        this(new File(filename));
    }

    public RandomAccessReadMemoryMappedFile(File file) throws IOException {
        this(file.toPath());
    }

    public RandomAccessReadMemoryMappedFile(Path path) throws IOException {
        fileChannel = FileChannel.open(path, StandardOpenOption.READ);
        size = fileChannel.size();
        mappedByteBuffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, size);
        unmapper = this::unmap;
    }

    public void close() throws IOException {
        unmapper.accept(mappedByteBuffer);
        fileChannel.close();
    }

    public void seek(long position) {
        mappedByteBuffer.position((int) position);
    }

    public long getPosition() {
        return mappedByteBuffer.position();
    }

    public int read() {
        return mappedByteBuffer.get();
    }

    public int read(byte[] b, int offset, int length) {
        return mappedByteBuffer.get(b, offset, length);
    }

    public long length() {
        return size;
    }

    public boolean isClosed() {
        return fileChannel.isOpen();
    }

    public boolean isEOF() {
        return mappedByteBuffer.position() == size;
    }

    private void unmap(ByteBuffer buffer) {
        if (buffer.isDirect()) {
            try {
                sun.misc.Unsafe unsafe = sun.misc.Unsafe.getUnsafe();
                long address = ((sun.nio.ch.DirectBuffer) buffer).address();
                unsafe.invokeCleaner(buffer);
                unsafe.freeMemory(address);
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }
    }

    private void checkClosed() throws IOException {
        if (isClosed()) {
            throw new IOException("Reader is closed");
        }
    }
}
