    {
        if (process != null)
        {
            if (outProxy != null)
            {
                outProxy.expectShutdown();
            }
            errProxy.expectShutdown();
            if (force)
            {
                process.destroyForcibly();
            }
            else
            {
                process.destroy();
            }
            if (outThread != null)
            {
                outThread.interrupt();
            }
            errThread.interrupt();
            process = null;
        }
    }

