            {
                try
                {
                    proc.waitFor(500, TimeUnit.MILLISECONDS);
                }
                catch (InterruptedException ignored)
                {
                }
                if (proc.isAlive())
                {
                    throw new ImplementationError("Process is still running");
                }
            }
            exitValue = proc.exitValue();
        }
        else
        {
            exitValue = PROCESS_STOPPED;
        }
