import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.OutputStreamWriter;
import java.nio.charset.Charset;
import java.util.concurrent.ForkJoinPool;
import java.util.concurrent.RecursiveAction;

public abstract class AbstractScriptProcessor {

    protected static final ForkJoinPool POOL = new ForkJoinPool();
    protected static final String SH_SHELL = "/bin/sh";
    protected static final String CMD_SHELL = "cmd.exe";

    public ProcessResult executeScript(TaskContext context, String scriptPath) {
        ProcessResult result = new ProcessResult();
        ProcessBuilder pb = new ProcessBuilder(getRunCommand());
        pb.redirectErrorStream(true);
        pb.redirectInput(ProcessBuilder.Redirect.PIPE);
        pb.redirectOutput(ProcessBuilder.Redirect.PIPE);
        try {
            Process process = pb.start();
            OutputStream os = process.getOutputStream();
            OutputStreamWriter osw = new OutputStreamWriter(os, getCharset());
            osw.write(context.getScriptInput());
            osw.flush();
            os.close();
            StringBuilder sb = new StringBuilder();
            POOL.invoke(new StreamCopyTask(process.getInputStream(), sb, context.getOmsLogger(), getCharset()));
            result.setExitCode(process.waitFor());
            result.setScriptOutput(sb.toString());
        } catch (Exception e) {
            context.getOmsLogger().error("Error executing script", e);
            result.setExitCode(-1);
            result.setScriptOutput(e.getMessage());
        }
        return result;
    }

    protected void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
        try (InputStreamReader isr = new InputStreamReader(is, charset)) {
            char[] buffer = new char[1024];
            int len;
            while ((len = isr.read(buffer)) > 0) {
                sb.append(buffer, 0, len);
            }
        } catch (Exception e) {
            omsLogger.error("Error copying stream", e);
        }
    }

    protected abstract String getRunCommand();

    protected Charset getCharset() {
        return Charset.forName("UTF-8");
    }

    private class StreamCopyTask extends RecursiveAction {

        private final InputStream is;
        private final StringBuilder sb;
        private final OmsLogger omsLogger;
        private final Charset charset;

        public StreamCopyTask(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
            this.is = is;
            this.sb = sb;
            this.omsLogger = omsLogger;
            this.charset = charset;
        }

        @Override
        protected void compute() {
            copyStream(is, sb, omsLogger, charset);
        }
    }
}
