        try {
            process.waitFor(10, TimeUnit.SECONDS);
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }

    public Collection<String> addJVMInvokeOptions(BenchmarkParams params) {
        List<String> perfOptions = new ArrayList<>();
