public class ShellCommand extends AbstractCommand {
    private String command;
    private List<String> argv;
    private Pattern shellWildcardPattern;

    public ShellCommand(String command) {
        this.command = command;
        this.argv = new ArrayList<>();
        this.shellWildcardPattern = Pattern.compile("[\\s|&;()<>{}`$*?\\[\\]]");
        this.argv.add(command);
        if (hasWildcard(command)) {
            this.argv.add(0, "/bin/sh");
            this.argv.add(0, "-c");
        }
    }

    private boolean hasWildcard(String command) {
        return shellWildcardPattern.matcher(command).find();
    }

    private List<String> tokenize(String s) {
        List<String> tokens = new ArrayList<>();
        StringTokenizer st = new StringTokenizer(s);
        while (st.hasMoreTokens()) {
            tokens.add(st.nextToken());
        }
        return tokens;
    }

    @Override
