        mProcessBuilder.command(scriptPath.toString(), libraryPath.toString());
    }

    public void execute(ProcessOutputHandler outputHandler) {
        mExecutor = ThreadPool.CACHED;
        mExecutor.execute(() -> {
            try {
                mProcess = mProcessBuilder.start();
                BufferedReader reader = new BufferedReader(new InputStreamReader(mProcess.getInputStream()));
                String line;
                while((line = reader.readLine()) != null) {
                    outputHandler.handleOutput(line);
                }

                int exitCode = mProcess.waitFor();
                outputHandler.handleCompletion(exitCode);
            } catch(InterruptedException ie) {
                outputHandler.handleError("Process was interrupted");
            } catch(IOException ioe) {
                outputHandler.handleError("IO Error during process execution");
            }
        });
    }

    public void terminate() {
        if(mProcess != null) {
            mProcess.destroy();
        }
        if(mExecutor != null) {
            mExecutor.shutdownNow();
        }
    }

    public interface ProcessOutputHandler {
        void handleOutput(String output);
        void handleCompletion(int exitCode);
        void handleError(String errorMessage);
