            } catch (IOException e) {
                Log.e("ProcessHandler", "Error reading process output", e);
            }
        });
    }

    public String runProcess(ProcessBuilder pb, boolean returnStdOut) {
        PowerManager.WakeLock wakeLock = ((PowerManager) mContext.getSystemService(Context.POWER_SERVICE)).newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, "ProcessHandler:WakeLock");
        wakeLock.acquire();
        try {
            Process process = pb.start();
            if (returnStdOut) {
                BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
                StringBuilder output = new StringBuilder();
                String line;
                while ((line = reader.readLine()) != null) {
                    output.append(line).append("\n");
                }
                return output.toString();
            }
            process.waitFor();
        } catch (IOException | InterruptedException e) {
            Log.e("ProcessHandler", "Error running process", e);
        } finally {
            wakeLock.release();
        }
        return null;
    }

    public List<String> getProcessPIDs(String processName) {
        List<String> pids = new ArrayList<>();
        try {
            Process process = Runtime.getRuntime().exec("ps");
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                if (line.contains(processName)) {
                    String[] parts = line.split("\\s+");
                    pids.add(parts[1]);
                }
            }
            if (mUseRoot) {
                Process rootProcess = Runtime.getRuntime().exec("su -c ps");
                BufferedReader rootReader = new BufferedReader(new InputStreamReader(rootProcess.getInputStream()));
                while ((line = rootReader.readLine()) != null) {
                    if (line.contains(processName)) {
                        String[] parts = line.split("\\s+");
                        pids.add(parts[1]);
                    }
                }
            }
